import React, { Component } from 'react';
import { connect } from 'react-redux';
import { 
  WriterWrapper,
  WriterTitle,
  WriterSwitch,
  WriterItem,
  WriterName,
  Follow
} from '../style';
import { getIn } from 'immutable';

class Writer extends Component {
  render() { 
    return (
      <WriterWrapper>
        <WriterTitle>
          推荐作者
          <WriterSwitch>
            换一批
          </WriterSwitch>
        </WriterTitle>
        <WriterItem>
          <img className="writer-img" alt="" src="" />
          <WriterName></WriterName>
          <p className="writer-count"></p>
          <Follow> +关注</Follow>
        </WriterItem>

      </WriterWrapper>
    );
  }
}

const mapState = (state) => ({
  list: getIn(['home'])
})
export default connect(mapState)(Writer);
