import styled from 'styled-components';
import logoPic from '../../statics/nav-logo.png'

export const HeaderWrapper = styled.div`
    position: relative;
    height: 56px;
    border-bottom: 1px solid #f0f0f0;
`;

export const Logo = styled.a`
    position: absolute;
    height: 56px;
    display: block;
    top: 0;
    left: 0;
    width: 100px;
    background: url(${logoPic});

`;
