import React, { Component } from 'react';
import { connect } from 'react-redux';
import { 
  WriterWrapper,
  WriterTitle,
  WriterSwitch,
  WriterItem,
  WriterName,
  Follow
} from '../style';


class Writer extends Component {
  render() { 
    const {list} = this.props;
    return (
      <WriterWrapper>
        <WriterTitle>
          推荐作者
          <WriterSwitch>
            换一批
          </WriterSwitch>
        </WriterTitle>
        {
          list.map((item) => {
            return (
              <WriterItem key = {item.get('id')}>
                <img className="writer-img" alt="" src={item.get('imgUrl')} />
                <WriterName>{item.get('name')}</WriterName>
                <p className="writer-count">{item.get('count')}</p>
                <Follow> +关注</Follow>
              </WriterItem>
            )
          })
        }

      </WriterWrapper>
    );
  }
}

const mapState = (state) => ({
  list: state.getIn(['home','writerList'])
})
export default connect(mapState,null)(Writer);
