import React, { Component } from 'react';
import { connect } from 'react-redux';
import banner from '../../statics/banner.jpeg';
import List from './components/List';
import Recommend from './components/Recommend';
import Topic from './components/Topic';
import Writer from './components/Writer';
import axios from 'axios';
import { 
  HomeWrapper,
  HomeLeft,
  HomeRight
} from './style';


class Home extends Component {
  render() { 
    return ( 
      <HomeWrapper>
        <HomeLeft>
          <img className="banner-img" alt="banner" src={banner}/>
          <Topic />
          <List />
        </HomeLeft>
        <HomeRight>
          <Recommend />
          <Writer />
        </HomeRight>
      </HomeWrapper>
    );
  }
  componentDidMount() {
      this.props.changeHomeData();  //把UI组件中的东西拿出来，拿到下面的容器组件中
  }
}

const mapDispatch = (dispatch) => ({
  changeHomeData() {
    axios.get('/api/home.json').then((res) => {
      const result = res.data.data;
      const action = {
        type: 'change_home_data',
        articleList: result.articleList,
        recommendList: result.recommendList,
        topicList: result.topicList,
        writerList: result.writerList
      }
    dispatch(action);  //派发action给store，store转发给reducer，不仅是大的reducer可以收到，它组成的每个小的reducer也可以收到
    })
  }
});
export default connect(null, mapDispatch)(Home);
