import axios from 'axios';


export const getDetail = () => {
  return (dispatch) => {
    axios.get('api/detail.json').then((res) => {
      const result = res.data.data;
      const action = changeDetail(result.title, result.content);
      dispatch(action); 
    })
  }
}
