import {TopicWrapper,TopicItem} from '../style'

class Topic extends Component {
  render() { 
    return (
      <TopicWrapper>
        {
          this.props.list.map((item) => {

          })
        }


        <TopicItem>
          <img 
            className='topic-pic'
            src="https://upload.jianshu.io/collections/images/1767718/crop1557956823393.jpg?imageMogr2/auto-orient/strip|imageView2/1/w/48/h/48/format/webp" 
          />
          社会热点
        </TopicItem>
      </TopicWrapper>  
    );
  }
}

const mapState = (state) => ({
  list: state.get('home').get('topicList')
});

export default connect(mapState,null)(Topic);  //mapState是从store里拿数据
