import styled from 'styled-components';
import logoPic from '../../statics/nav-logo.png'

export const HeaderWrapper = styled.div`
    position: relative;
    height: 56px;
    border-bottom: 1px solid #f0f0f0;
`;

export const Logo = styled.a.attrs({href:'/'})`
    position: absolute;
    height: 56px;
    display: block;
    top: 0;
    left: 0;
    width: 100px;
    background: url(${logoPic});
    background-size: contain;   
`;

export const Nav = styled.div`
    height: 100%;
    width: 960px;
    margin: 0 auto;
    padding-right: 70px;
    box-sizing: border-box;
`;

export const NavItem = styled.div`
    line-height: 56px;
    padding: 0 15px;
    font-size: 17px;
    color: #333;
    &.left {
        float: left;
    }
    &.right {
        float: right;
        color: #969696;
    }
    &.active {
        color: #ea6f5a;
    }
`

export const NavInput = styled.input.attrs({
    placeholder: '搜索'
})`
    width: 160px;
    height: 38px;
    border-radius: 19px;
    background: #eee;
    padding:0 20px;
    box-sizing: border-box;
    border: none;
    outline: none;
    margin-top: 9px;
    margin-left: 20px;
    font-size: 14px;
    &::placeholder {
        color: #999;
    }
`;

export const Addition = styled.div` 
    position: absolute;
    height: 56px;
    top: 0;
    right: 0;
`;
export const Button = styled.div`
    height: 38px;
    border: 1px solid #ea6f5a;
    border-radius: 19px;
    line-height: 38px;
    padding: 0 20px;
    margin-top: 9px;
    margin-right: 5px;
    &.reg {
        float: left;
    }
    &.writing {
         float: right;
    }
`;
